import { Component, OnInit } from '@angular/core';
import { PostService } from '../post.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router, Params } from '@angular/router';
import { Post } from '../post.model';

@Component({
  selector: 'app-post-edit',
  templateUrl: './post-edit.component.html',
  styleUrls: ['./post-edit.component.css']
})
export class PostEditComponent {
form!: FormGroup;
  index: number = 0;
constructor(private postService:PostService, private router: Router, private actroute:ActivatedRoute){}

ngOnInit():void{
  this.form=new FormGroup({
    title:new FormControl(null,[Validators.required]),
  imgPath:new FormControl(null,[Validators.required]),
  description:new FormControl(null,[Validators.required])
});
let title="";
let imgPath="";
let description="";
this.actroute.params.subscribe((params:Params) => {

  if(params['index']){
    const postSpec=this.postService.getSpecPost(this.index);
  }
})
}
onSubmit(){
  const title=this.form.value.title;
const imgPath=this.form.value.imgPath;
const description=this.form.value.description;

const post: Post=new Post(title,imgPath,description,'jc',new Date(),9);

this.postService.addPost(post);
this.router.navigate(['/post-list'])
}
}
