import { Component } from '@angular/core';
import { Post } from '../post.model';

@Component({
  selector: 'app-post-list',
  templateUrl: './post-list.component.html',
  styleUrls: ['./post-list.component.css']
})
export class PostListComponent {
listOfPost:Post[]=[
  new Post("vertex",
  "https://www.hostinger.com/tutorials/wp-content/uploads/sites/2/2021/12/the-verge-website-homepage.webp",
  "Me",
  "The Verge is a blog focused on examining how technology will change the future",
  new Date())
];
}
<app-post *ngFor="let post of listOfPost"[post]="post"></app-post>
