import { Injectable } from '@angular/core';
import { PostService } from './post.service';
import { Post } from './post.model';
import { HttpClient } from '@angular/common/http';
import { tap } from 'rxjs';
import { BackEndService } from './back-end.service'; // Adjust the path as needed
import { Post } from './post.model';

@Injectable({
  providedIn: 'root'
})
export class BackEndService implements OnInit {
  listOfPosts: Post[];

  constructor(private postService: PostService, private http: HttpClientrivate backEndService: BackEndService) { }
  ngOnInit(): void {
    this.backEndService.fetchData().subscribe((posts: Post[]) => {
      this.listOfPosts = posts;
    });
  }
}

  saveData() {
    const listOfPosts: Post[] = this.postService.getPost();
    this.http.put(
      'https://gutierrezfirebase-default-rtdb.asia-southeast1.firebasedatabase.app/posts.json',
      listOfPosts)
      .subscribe((res) => {
        console.log(res)
      })
  }
  fetchData() {
    this.http.get<Post[]>(
      'https://gutierrezfirebase-default-rtdb.asia-southeast1.firebasedatabase.app/posts.json')
      .pipe
      (tap((listOfPosts: Post[]) => {
        console.log(listOfPosts)
        this.postService.setPosts(listOfPosts);
      })).subscribe();
  }
}
