import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service'; // Service that handles authentication

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html', // Location of the corresponding HTML template
  styleUrls: ['./register.component.css'] // Location of the corresponding CSS styles
})
export class RegisterComponent implements OnInit {
  registerForm: FormGroup;
  loading = false;
  submitted = false;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private authService: AuthService
  ) { }

  ngOnInit() {
    this.registerForm = this.formBuilder.group({
      email: ['', Validators.required],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  // Convenience getter for easy access to form fields
  get f() { return this.registerForm.controls; }

  onSubmit() {
    this.submitted = true;

    // Stop here if form is invalid
    if (this.registerForm.invalid) {
      return;
    }

    this.loading = true;
    this.authService.register(this.f.email.value, this.f.password.value)
      .subscribe(
        data => {
          // Navigate to login page, etc.
        },
        error => {
          this.loading = false;
          // Handle error, etc.
        });
  }
}
