import { Injectable } from '@angular/core';
import { PostService } from './post.service';
import { Post } from './post.model';
import { HttpClient } from '@angular/common/http';
import { tap } from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class BackEndService {
constructor(private postService: PostService, private http: HttpClient) { }
saveData(){
  const listOfPosts: Post[] = this.postService.getPost();
  this.http.put(
    'https://gutierrez-firebase-default-rtdb.asia-southeast1.firebasedatabase.app/',listOfPosts)
    .subscribe((res)=>{
      console.log(res)
    })
}
fetchData(){
  this.http.get<Post[]>('https://fir-gutierrez-default-rtdb.asia-southeast1.firebasedatabase.app/')
  .pipe
  (tap((listOfPosts:Post[])=>{
    console.log(listOfPosts)
  })).subscribe();
}
}
